      return [value => !!value || `You must enter a grade ${gradeNumber}`, value => value && !isNaN(value) && value >= 0 && value <= 10 || `Grade ${gradeNumber} must be a number between 0 and 10`];
    },
    async validate() {
      const {
        valid
      } = await this.$refs.form.validate();
      if (valid) {
        const media = parseFloat(this.grade1 + this.grade2 + this.grade3 + this.grade4) / 4;
        this.averageCalculation();
        this.$refs.form.reset();
        this.$refs.form.resetValidation();
      }
    },
    averageCalculation() {
      const g1 = Number(this.grade1);
      const g2 = Number(this.grade2);
      const g3 = Number(this.grade3);
      const g4 = Number(this.grade4);
      const average = (g1 + g2 + g3 + g4) / 4;
      const status = average >= 7 ? 'Aproved' : 'Failed';
      alert(`Average: ${average} - ${status}`);
    }
  }
};
