export default {
  data: () => ({
    name: '',
    nameRules: [value => !!value || 'Name is required'],
    grade1: null,
    grade2: null,
    grade3: null,
    grade4: null,
    snackbar: {
      visibility: false,
      message: '',
      color: ''
    }
  }),
  methods: {
    gradeValidation(gradeNumber) {
      return [value => !!value || `You must enter a grade ${gradeNumber}`, value => value && !isNaN(value) && value >= 0 && value <= 10 || `Grade ${gradeNumber} must be a number between 0 and 10`];
    },
    async validate() {
      const {
        valid
      } = await this.$refs.form.validate();
      if (valid) {
        const average = this.averageCalculation();
        const status = this.checkStatus();
        if (status) {
          this.snackbar.message = `Average: ${average} - Status: Approved`;
          this.snackbar.color = 'green';
        } else {
          this.snackbar.message = `Average: ${average} - Status: Failed`;
          this.snackbar.color = 'red';
        }
        this.$refs.form.reset();
        this.$refs.form.resetValidation();
      }
    },
    averageCalculation() {
      const grades = [this.grade1, this.grade2, this.grade3, this.grade4].map(grade => Number(grade));
      const total = grades.reduce((sum, grade) => sum + grade, 0);
      const average = total / grades.length;
      return average;
    },
    checkStatus() {
      const average = this.averageCalculation();
      const status = average >= 7 ? true : false;
      return status;
    }
  }
};
