export default {
  props: {
    student: Object,
    visible: Boolean
  },
  data() {
    return {
      localStudent: {
        ...this.student
      },
      // Cria uma cópia local dos dados do estudante
      localVisible: this.visible // Usa uma variável local para controlar a visibilidade
    };
  },
  watch: {
    visible(newValue) {
      this.localVisible = newValue; // Sincroniza a visibilidade local com a prop
    },
    student(newValue) {
      this.localStudent = {
        ...newValue
      }; // Sincroniza o estudante local com a prop
    }
  },
  methods: {
    close() {
      this.$emit('cancel');
    },
    save() {
      if (this.$refs.form.validate()) {
        this.$emit('save', this.localStudent);
      }
    }
  }
};
